void GetSubstMx_Letter_Blosum(uint PctId, float Mx[20][20])
	{
#define X(x)										\
	case x:											\
	for (unsigned i = 0; i < 20; ++i)				\
		for (unsigned j = 0; j < 20; ++j)			\
			Mx[i][j] = Blosum##x##_sij[i][j];		\
	return;

	switch (PctId)
		{
		X(90);
		X(70);
		X(80);
		X(62);
		}
#undef X
	Die("SetSubstMx_Letter_Blosum(%u)", PctId);
	}

