float GetBlosumScoreChars(byte a, byte b)
	{
	uint ia = g_CharToLetterAmino[a];
	uint ib = g_CharToLetterAmino[b];
	if (ia >= 20 || ib >= 20)
		return 0;
	return Blosum62_sij[ia][ib];
	}

float GetBlosumScoreLetters(byte ia, byte ib)
	{
	if (ia >= 20 || ib >= 20)
		return 0;
	return Blosum62_sij[ia][ib];
	}
